import { describe } from 'react-desc';

export default (function (Element) {
  var DocumentedElement = describe(Element).description('Line chart wrapper to chart.js Line with grommet theming.\n       For all standard charting options, consult the chart.js docs\n      ').usage('\n      $ npm install grommet-controls\n \n      import { LineChart } from \'grommet-controls/chartjs\';\n\n      <LineChart data={...} options={...} />\n    ');
  return DocumentedElement;
});